mapper.select:
SELECT COUNT(1) FROM t_penguin_question WHERE state = 1
AND content LIKE CONCAT(CONCAT('%', #{content}), '%')
AND difficulty = #{difficulty}

SELECT * FROM t_penguin_question WHERE state = 1
AND content LIKE CONCAT(CONCAT('%', #{content}), '%')
AND difficulty = #{difficulty}

mapper.update:
UPDATE t_penguin_question
SET
  state = #{state}
WHERE
contest_id = #{contestId}
